import React, {useState, useContext} from 'react';
import './AuthPage.scss'
import {BrowserRouter, Switch, Route, Link, useHistory} from 'react-router-dom';
import {AuthContext} from '../../context/AuthContext'
import Axios from '../utils'

function AuthPage() {
    const [form, setForm] = useState({email: '', password: ''});
    const history = useHistory()
    const { login } = useContext(AuthContext)

    const handleChange = event => {
        setForm({...form, [event.target.name]: event.target.value})
    }

    // const handleSubmit = async (e) => {
    //     e.preventDefault()
    //     await Axios.post('/auth/registration', {...form})
    //     .then(res => console.log(res))
    // }
    // const handleSubmit = async (e) => {
    //     e.preventDefault()
    //     try {
    //         await axios.post('/api/auth/registration', { ...form }, {
    //             headers: {
    //                 'Content-Type': 'application/json'
    //             }
    //         }).then(res => console.log(res))
    //     } catch(err) {
    //         console.log(err);
    //     }
    // }

        const handleSubmit = async (e) => {
            e.preventDefault()
            await Axios.post('/auth/registration', {...form})
            history.push('/')
            // .then(res => console.log(res))
        }

        const handleLogin = async () => {
            await Axios.post('/auth/login', {...form})
            .then(res => login(res.data.token, res.data.userId))
        }

        // const handleLogin = async () => {
        //     try {
        //         await axios.post('/api/auth/login', { ...form }, {
        //             headers: {
        //                 'Content-Type': 'application/json'
        //             }
        //         })
        //         .then(res => login(res.data.token, res.data.userId))
        //     } catch (e) {
        //         console.log(e);
        //     }
        // }


    return(
        <BrowserRouter>
            <Switch>
                <>
                <a class="waves-effect waves-light btn modal-trigger" href="#modal1">Modal</a>

<div id="modal1" class="modal">
  <div class="modal-content">
    <h4>Modal Header</h4>
    <p>A bunch of text</p>
  </div>
  <div class="modal-footer">
    <a href="#!" class="modal-close waves-effect waves-green btn-flat">Agree</a>
  </div>
</div>
                <div className="container">
                    <div className="auth-page">
                        <Route path="/login">
                            <h3>Авторизация</h3>
                            <form onSubmit={(e) => e.preventDefault()} className="form form-login">
                                <div className="row">
                                    <div className="input-field col s12">
                                        <input
                                            onChange={handleChange}
                                            type="text" 
                                            name="email" 
                                            className="validate"
                                        />
                                        <label htmlFor="email">Email</label>
                                    </div>
                                    <div className="input-field col s12">
                                        <input
                                            onChange={handleChange}
                                            type="password" 
                                            name="password" 
                                            className="validate"
                                        />
                                        <label htmlFor="password">Password</label>
                                    </div>
                                </div>
                                <div className="row">
                                    <button onClick={handleLogin} className="wawes-effect wawes-light btn blue">
                                        Войти
                                    </button>
                                    <Link 
                                        className="btn-outline btn-reg" 
                                        to="/registration"
                                    >
                                        Нет аккаунта? Зарегистрироваться
                                    </Link>
                                </div>
                            </form>
                        </Route>
                    
                        <Route path="/registration">
                            <h3>Регистрация</h3>
                            <form onSubmit={handleSubmit} className="form form-login">
                                <div className="row">
                                    <div className="input-field col s12">
                                        <input 
                                            onChange={handleChange}
                                            type="text" 
                                            name="email" 
                                            className="validate"
                                        />
                                        <label htmlFor="email">Email</label>
                                    </div>
                                    <div className="input-field col s12">
                                        <input
                                            onChange={handleChange}
                                            type="password" 
                                            name="password" 
                                            className="validate"
                                        />
                                        <label htmlFor="password">Password</label>
                                    </div>
                                </div>
                                <div className="row">
                                    <button className="wawes-effect wawes-light btn blue">
                                        Регистрация
                                    </button>
                                    <Link 
                                        className="btn-outline btn-reg" 
                                        to="/login"
                                    >
                                        Уже есть аккаунт? Войти
                                    </Link>
                                </div>
                            </form>
                        </Route>
                    
                    </div>
                </div>
            </>
            </Switch>
        </BrowserRouter>
    
    )
}

export default AuthPage;
